SUBSCRIPTIONS.QUERY:
subscription{
  queryWorkflowSpecification{
    id
    isActive
    name
    nodes{
      id
      uiComponent
    form{
  name
    }
   isActive
      name
      description
      nodeType{
        id
        name
      nodeClass
}
      properties{
        id
        name
        dataType
      }
      inflows {
        from{id,isActive}
      }
      outflows{
        to{id,isActive}
        condition
      }
    }
  }
}

subscription{
  queryWorkflowInstance(filter:{not:{status:{eq:COMPLETED}}})@cascade(fields:"nodeInstances"){
    id
    status
    workflowSpecification{id,name}
    nodeInstances(filter:{not:{status:{eq:COMPLETED}}})@cascade(fields:"node"){
      id
      status
      node @cascade(fields:"role"){
        id
      	name
        form{name}
        uiComponent
        role(filter:{name:{eq:"Maintenance"}}){
          name
        }
      }
    }
  }
}

subscription{
  queryWorkflowInstance(filter:{not:{status:{eq:COMPLETED}}}){
    id
    status
    workflowSpecification{id,name}
    nodeInstances(filter:{not:{status:{eq:COMPLETED}}}){
      id
      status
      node{
        id
      	name
        form{name}
        uiComponent
        role{
          name
        }
      }
    }
  }
}

subscription{
  queryWorkflowNodeInstance(filter:{not:{status:{eq:COMPLETED}}}){
    id
