subscription{
  queryEquipment{
    id
    workflowInstances(filter:{not:{status:{eq:COMPLETED}}}){
      id
      workflowSpecification{name}
      status
      nodeInstances(filter:{not:{status:{eq:COMPLETED}}}){
        id
        status
        node{
          id
          name
          uiComponent
          form{
            name
            jsonSchema
            uiSchema
          }
        }
        events(order:{desc:createdDateTime} first:1){createdDateTime}
      }
      propertyInstances{
        workflowProperty{name}
        values(order:{desc:createdDateTime} first:1){value}
      }
    }
  }
}

subscription{
  queryEquipment{
    id
    propertyNameAliases{
      property{name}
      alias
      system
    }
  }
}

subscription{
  queryEquipment(filter:{not:{isActive:false},equipmentLevel:{in:[ProductionLine,ProductionUnit,ProcessCell,WorkCell,WorkCenter,WorkUnit,ControlModule,EquipmentModule]}}){
    id
    name
    dataProvider
    equipmentClass{
      name
    }
    equipmentLevel  
  }
}

subscription{
  queryEquipment (filter:{not:{isActive:false}}) @cascade(fields:["propertyNameAliases"]){
    id
    name
    dataProvider
    dataProviderConnection
    addressTemplate
    equipmentClass{
  id
    properties{
      id
     expression 
 type 
 name
      description
      dataType
      storeHistory
      unitOfMeasure{code}
    }
      eventDefinitions(filter:{not:{isActive:false}}){
        id
        name
        messageClass
        delayTime 
 delayOption 
   triggerProperties{
          id
          name
        }
        triggerExpression
        payloadProperties{
          id
          name
        }
        payloadFields{
      id
    name
          expression
        }
      }
    }
    propertyNameAliases{
   id
   system
      alias
      property(filter:{type:{eq:BOUND}}){
    id
 name
}
    }
    propertyOverrides{
   id
   property(filter:{type:{eq:BOUND}}){
        id
        name
      }
      ignore
    } 
  }
}
